  dc:title "@@TEXTO-título-idioma1@@"@es, "@@TEXTO-título-idioma2@@"@en ;
  dc:description "@@TEXTO-descripción-idioma1@@"@es, "@@TEXTO-descripción-idioma2@@"@en ;
